function Map(){
  var engBuildingCoords =[
    {lat: 37.3377, lng: -121.8815},
    {lat: 37.3372, lng: -121.8825},
    {lat: 37.3363, lng: -121.8819},
    {lat: 37.3368, lng: -121.8809},
  ];
  //this.state = {visible: false};
  //console.log(this.state.visible);
  this.showDrawer = () => {
    this.setState({visible: true});
  };
  this.onClose =()=>{
    this.setState({visible:false});
  };
  return(
    <GoogleMap
      defaultZoom={17}
      defaultCenter={{lat: 37.3352, lng: -121.8811}}
      // center={{lat: 37.272040, lng: -121.861260}}
      options={{ styles: [{ elementType: "labels", featureType: "poi.business", stylers: [{ visibility: "off", }], }], }}
      >
      <Polygon
        paths= {engBuildingCoords}
        options={{ 
          strokeColor: '#36688F',
          strokeOpacity: 0.8,
          strokeWeight: 3,
          fillColor: '#36688F',
          fillOpacity: 0.35 
        }}
        onClick={this.showDrawer}
        // strokeColor={'pink'}
        // strokeOpacity={ 0.8}
        // strokeWeight= {3}
        // fillColor={'yellow'}
        // fillOpacity= {0.35}
      />
      <Drawer
      title="Basic Drawer"
      placement="left"
      closable={false}
      onClose={this.onClose}
      visible={this.state}
    >
      <p>Some contents...</p>
      <p>Some contents...</p>
      <p>Some contents...</p>
    </Drawer>
    </GoogleMap>

    // <Drawer
